// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract Karma {
  address admin;
  mapping(address => uint) public karmaMap;
  mapping(address => bool) public isMoneyMarket;
  constructor() public {
    admin = msg.sender;
    isMoneyMarket[0xfd57514113A708A72acB17811786A7af2759Ce2D] = true;
  }

  function getKarma() public view returns (uint karma)
  {
      karma = karmaMap[msg.sender];
  }
  function raiseKarma(address addr, uint amount) public {
      require(isMoneyMarket[msg.sender], "Sender not authorized.");
      karmaMap[addr] += amount;
  }
  function lowerKarma(address addr, uint amount) public {
      require(isMoneyMarket[msg.sender]);
      karmaMap[addr] -= amount;
  }
  function addMoneyMarket(address addr) public {
      require(msg.sender == admin);
      isMoneyMarket[addr] = true;
  }
  function removeMoneyMarket(address addr) public {
      require(msg.sender == admin);
      isMoneyMarket[addr] = false;
  }
}
